import React from "react";
import "./Product.css";

class Product extends React.Component {
  render() {
    const { description, price, name } = this.props.product;

    return (
      <div className="product">
        <h3>{name}</h3>
        <p>{description}</p>
        <P>{price}</P>
      </div>
    );
  }
}

export default Product;
