import React from "react";

class Product extends React.Component {
  render() {
    const { name, category, description, price } = this.props.product;
    return (
      <div className="product">
        <h3>{name}</h3>
        <p>Description: {description}</p>
        <p>Price: ${price}</p>
      </div>
    );
  }
}

export default Product;
