import React from "react";
import "./Product.css";

//This component is rendering the product information for each product in the product list.  It is receiving the product information as props from the ProductList component. The product information is then rendered in the return statement.
class Product extends React.Component {
  render() {
    const { name, description, price } = this.props.product;
    return (
      <div className="product">
        <h3>{name}</h3>
        <p>Description: {description}</p>
        <p>Price: ${price}</p>
      </div>
    );
  }
}

export default Product;
